class Box(PlottingTest):

    @dynamic_test
    def test(self):
        pr = TestedProgram()
        output = pr.start().replace(" ", "").lower()

        all_figures = self.all_figures()

        if len(all_figures) == 0:
            raise WrongAnswer("Looks like you didn't present any plots")
        if len(all_figures) != 1:
            raise WrongAnswer(f"Expected 1 image of age distribution across Nobel prize categories")

        graph1_type = all_figures[0].type
        if graph1_type != "box":
            raise WrongAnswer("The type of the plot is wrong. The boxplot is expected.")

        graph1_data_y = all_figures[0].data.y.tolist()

        if len(graph1_data_y) != len(dict_img1_data_CORRECT["yaxis"]):
            raise WrongAnswer("Wrong number of categories displayed in box plot")

        for j in range(len(dict_img1_data_CORRECT["yaxis"])):
            if not graph1_data_y[j] == dict_img1_data_CORRECT["yaxis"][j]:
                raise WrongAnswer(f"Wrong age data for the {j} category")
        return CheckResult.correct()


if __name__ == '__main__':
    Box().run_tests()